clc clear all; close all; pkg load signal; t=linspace(0,1,1000); A=0.5; fc=1; m_t=A*sin(2*pi*t*fc); s=0.1; %step size s_t=0; v=1; mqc_t=0; for i=1:10:length(t)     if(m_t(1,i)>mqc_t(1,v))        s_t(1,v)=1        mqc_t(1,v+1)=mqc_t(1,v)+s;     else        s_t(1,v)=0;        mqc_t(1,v+1)=mqc_t(1,v)-s;     end     t1(1,v)=t(1,i);     v=v+1;  end  subplot(2,1,1)  plot(t,m_t);  title('1904428-(DM)Mayukh');  hold on  stairs(t1,mqc_t(2:101),'r')  subplot(2,1,2)  stairs(s_t)  axis([0 100 -0.5 1.5])    title('1904428-Mayukh(Output)');